pragma solidity ^0.5.0;

import "@openzeppelin/contracts/crowdsale/Crowdsale.sol";
import "./Kyc.sol";
contract DiamondTokenSale is Crowdsale {
    Kyc kyc;
    constructor(
        uint256 rate, 
        address payable wallet, 
        IERC20 token)
        public Crowdsale(rate, wallet, token)   {    
        // rate in TKNbits 
