    function _preValidatePurchase(address beneficiary, uint256 weiAmount) internal view {
    super._preValidatePurchase(beneficiary, weiAmount);
    require(kyc.kycCompleted(beneficiary), "Kyc not completed yet, aborting");
    }

}
